import Notice from "../models/Notice";

//검색기능
export const search = async (req, res) => {
  const {
    query: { title },
  } = req;
  let notice = [];
  if (title) {
    notice = await Notice.find({
      title: {
        $regex: new RegExp(`${title}`, "i"),
      },
    });
  }
  return res.render("notice/search", { pageTitle: "Search Title", notice });
};

//Notice Router
export const getUpload = async (req, res) => {
  return res.render("notice/upload", { pageTitle: "Upload Notice" });
};

export const postUpload = async (req, res) => {
  const {
    body: { title, content },
    file,
  } = req;
  //이미지 파일을 업로드 할 경우
  if (file) {
    await Notice.create({
      title,
      description: content,
      image: file.path,
    });
  }
  //이미지 파일을 업로드 안할경우
  else {
    await Notice.create({
      title,
      description: content,
    });
  }
  return res.redirect("/");
};

export const see = async (req, res) => {
  const {
    params: { id },
  } = req;
  try {
    const notice = await Notice.findById(id);
    await Notice.findByIdAndUpdate(id, {
      meta: {
        views: notice.meta.views + 1,
      },
    });
    return res.render("notice/see", { pageTitle: `${notice.title}`, notice });
  } catch (error) {
    console.log(error);
  }
};

export const getEdit = async (req, res) => {
  const {
    params: { id },
  } = req;
  const notice = await Notice.findById(id);
  return res.render("notice/edit", {
    pageTitle: `Edit : ${notice.title}`,
    notice,
  });
};

export const postEdit = async (req, res) => {
  const {
    body: { title, content },
    params: { id },
    file,
  } = req;
  const exists = await Notice.exists({ _id: id });
  if (!exists) {
    return res.redirect("/");
  }
  if (file) {
    await Notice.findByIdAndUpdate(
      id,
      {
        title,
        description: content,
        image: file.path,
      },
      { new: true }
    );
  } else {
    await Notice.findByIdAndUpdate(id, {
      title,
      description: content,
    });
  }
  return res.redirect("/");
};

export const deleteNotice = async (req, res) => {
  const {
    params: { id },
  } = req;
  await Notice.findByIdAndDelete(id);
  return res.redirect("/");
};

export const getReport = async (req, res) => {
  const {
    params: { id },
  } = req;
  const notice = await Notice.findById(id);
  return res.render("notice/report", { pageTitle: `Report Notice`, notice });
};
